import { useEffect, useState } from "react";
import axios from "axios";
import jwtDecode from "jwt-decode";
import UserContext from "./context/UserContext";

//Conexión API
import { getPokemons, getPokemonData } from "./services/api";

//Componentes
import PokeCard from "./components/PokeCard";
import RegisterForm from "./components/Auth/RegisterForm/RegisterForm";
import LoginForm from "./components/Auth/LoginForm";

function App() {
  const [trigerPokedex, setTrigerPokedex] = useState(true);
  const [pokemons, setPokemons] = useState([]);
  const [decodeToken, setDecodeToken] = useState({});
  const [token, setToken] = useState(null);
  const [showLogin, setShowLogin] = useState(true);
  console.log(trigerPokedex);

  useEffect(() => {
    fetchPokemons();
    getTokenLocalStorage();
  }, [trigerPokedex]);

  const fetchPokemons = async () => {
    try {
      const data = await getPokemons();
      const promises = data.results.map(async (pokemon) => {
        return await getPokemonData(pokemon.url);
      });
      const result = await Promise.all(promises);
      setPokemons(result);
      console.log(result);
    } catch (error) {
      console.log(error);
    }
  };
  // Simulamos que traemos información del usuario
  const userData = {
    username: "Ricardo",
    school: "Socratech",
    year: 2021,
  };
  // Register user
  /*   const newUser = () => {
    axios
      .post("http://localhost:4000/users/saveUser", {
        name: "Pocha",
        user_name: "pochita",
        password: "123",
      })
      .then((response) => {
        console.log(response);
      });
  }; */

  const TOKEN = "token";
  // Login user
  //Esta función conecta a través de axios la ruta post de login en la que el usuario introduce su user_name y su password y tras loguearse se ejecuta el .then que es un proceso "automático" que hay que hacer así en el que se codifica el token con jwtDecode, se muestra por pantalla, se recoge en el logalStorage la palabra token y el token propiamente dicho y se setea en el localStorage.

  /*   const loginUser = () => {
    axios
      .post("http://localhost:4000/users/login", {
        user_name: "pochita",
        password: "123",
      })
      .then((response) => {
        setDecodeToken(jwtDecode(response.data.token));
        console.log(jwtDecode(response.data.token));
        localStorage.setItem(TOKEN, response.data.token);
        setToken(localStorage.getItem(TOKEN));
      });
  }; */

  //Esta función reconoce el logueo de un usuario mientras no cierre sesión. Su token seguirá en el localStorage hasta que no se desloguee.
  const getTokenLocalStorage = () => {
    setToken(localStorage.getItem(TOKEN));
  };

  //Esta función es para que el usuario se desloguee
  const logout = () => {
    localStorage.removeItem(TOKEN);
    setToken(null);
  };

  //localhost:4000/pokemon/:user_id/:pokemon_id

  // Guardar pokemon por usuario
  const newPokemon = (pokemonID) => {
    const decodeUser = jwtDecode(token);
    const userID = decodeUser.user.id;
    axios
      .post(`http://localhost:4000/pokemon/${userID}/${pokemonID}`, {})
      .then((response) => {
        console.log(response);
      });
  };

  //Esto ya es la "vista" que se muestra al usuario con el "html" (jsx)
  return (
    <UserContext.Provider value={userData}>
      <div className="App">
        <button onClick={() => setTrigerPokedex(!trigerPokedex)}>
          Descubre Pokemones
        </button>
        {/*        <button onClick={newUser}>Crear usuarios</button>
        <button onClick={loginUser}>Login usuarios</button> */}
        <button onClick={logout}>Cerrar sesión</button>

        {pokemons.map((pokemon, idx) => {
          return (
            /*  <div key={idx}>
              <p>Nombre: {pokemon.name}</p>
              <p>Peso: {pokemon.weight}</p>
              <button onClick={() => newPokemon(pokemon.id)}>
                Agregar pokemon
              </button>
            </div> */
            <PokeCard key={idx} pokemon={pokemon} />
          );
        })}

        {token
          ? "Bieeeeeeeeen, te has registrado correctamente!"
          : "Mal, no te has registrado correctamente"}
        {showLogin ? (
          <LoginForm setShowLogin={setShowLogin} />
        ) : (
          <RegisterForm setShowLogin={setShowLogin} />
        )}
      </div>
    </UserContext.Provider>
  );
}

export default App;
